import React, { Component } from "react";
import { Link } from "react-router-dom";
import "../css/style.css";
import Logo from "../img/IUSlogo2.png";
import Cookie from "js-cookie";
import axios from "axios";

export default class Navigation extends Component {
  constructor(props) {
    super(props);

    this.logout = this.logout.bind(this);

    this.state = {
      isLogin: false,
      fullName: "",
    };
  }

  async componentDidMount() {
    /*Check if the user is properly authenticated*/
    if (!Cookie.get("token")) {
      window.location("/");
      return;
    }

    const response = await fetch(
      "http://localhost:5000/users/auth?token=" +
        Cookie.get("token") +
        "&userId=" +
        Cookie.get("userId")
    );

    if (response.status === 200)
      this.setState({
        buttons: (
          <Button variant="success" style={btnStyle} onClick={this.logout}>
            logout
          </Button>
        ),
      });
    else {
      this.setState({
        buttons: (
          <div>
            <Button variant="success" style={btnStyle} href="/login">
              Login
            </Button>
            <Button variant="outline-success" style={btnStyle} href="/signup">
              Sign up
            </Button>
          </div>
        ),
      });
    }
    this.setState({
      fullName: Cookie.get("name") + " " + Cookie.get("surname"),
    });
  }

  async componentDidMount() {
    const response = await fetch(
      "http://localhost:5000/users/auth?token=" +
        Cookie.get("token") +
        "&userId=" +
        Cookie.get("userId")
    );
  }

  logout() {
    axios
      .get(
        "http://localhost:5000/users/logout?token=" +
          Cookie.get("token") +
          "&userId=" +
          Cookie.get("userId")
      )
      .then((res) => {
        Cookie.remove("token");
        Cookie.remove("userId");
        window.location = "/";
      })
      .catch((err) => {
        /*alert("Error: " + err);*/ //Very strange beacause we considere it has an error but it is not...
        Cookie.remove("token");
        Cookie.remove("userId");
        window.location = "/";
      });
  }

  render() {
    return (
      <nav className="navigation">
        <div className="logo">
          <img className="ius" src={Logo} />
          <span
            className="user"
            dangerouslySetInnerHTML={{ __html: this.state.fullName }}
          ></span>
        </div>
        <div className="upButtons">
          <div className="links">
            <Link id="home" to="/Home">
              <span data-hover="Home">Home</span>
            </Link>
            <Link id="MyCourses" to="/UserCourses">
              <span data-hover="MyCourses">My courses</span>
            </Link>
            <Link id="AddCourses" to="/Courses">
              <span data-hover="AddCourses">Add courses</span>
            </Link>
            <Link id="Schedule" to="/Schedule">
              <span data-hover="Schedule">Schedule</span>
            </Link>
          </div>
        </div>
        <div className="downButtons">
          <div className="links">
            <Link id="LogOut">
              <form>
                <button onClick={this.logout} data-hover="LogOut">
                  Log Out
                </button>
              </form>
            </Link>
          </div>
        </div>
      </nav>
    );
  }
}
