import React, { Component } from "react";
import axios from "axios";
import { Card } from "react-bootstrap";
import Cookie from "js-cookie";
import "../css/style.css";

export default class AdminStudents extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: [],
      course: "",
      userId: Cookie.get("userId"),
    };
  }

  async componentDidMount() {
    await this.updateTable();
  }

  async updateTable() {
    axios
      .get("http://localhost:5000/debug/users")
      .then((res) => {
        this.setState({
          data: res.data[0].information,
        });
        console.log(res.data[0].information);
      })
      .catch((err) => alert("Error: " + err));
  }

  render() {
    return (
      <div class="addCourses">
        <div class="menu">
          <div class="line1"></div>
          <div class="line2"></div>
          <div class="line3"></div>
        </div>
        <div class="upperNav">
          <div class="search">
            <form class="searchForm">
              <input type="search" name="search" placeholder="Search" />
            </form>
            <button class="magnifier">
              <img src="../img/magnifier.png" class="manifierImg" />
            </button>
          </div>
          <div class="prevNext"></div>
        </div>
        <div class="addCoursesDownNav">
          <div class="typeOfCourses">
            <a class="blueBorder" id="marginZero" href="addCourses.html">
              All Students
            </a>
          </div>
        </div>
        <div class="allCourses" id="allCourses">
          <table class="courses">
            <tbody>
              <tr class="info">
                <td>ID</td>
                <td>Student Name</td>
                <td>Faculty</td>
                <td>Program</td>
                <td>Delete</td>
                <td>Edit</td>
              </tr>
              <tr>
                <td class="title">170302071</td>
                <td>Adem Dugalić</td>
                <td>FENS</td>
                <td>CSE</td>
                <td>
                  <div class="button">
                    <button>Delete</button>
                  </div>
                </td>
                <td>
                  <div class="button">
                    <button>Edit</button>
                  </div>
                </td>
              </tr>
              {Array.isArray(this.state.data) &&
                this.state.data.map((item, i) => {
                  return (
                    <tr key={i}>
                      <td className="title">{item.course_id}</td>
                      <td>
                        <a
                          className="ecampus"
                          href={"https://ecampus.ius.edu.ba/" + item.Url}
                        >
                          {item.course_name}
                        </a>
                      </td>
                      <td>{item.Lecturer}</td>
                      <td>{item.AcademicUnit}</td>
                      <td>
                        {item.prerequisite.map((item) => {
                          return item + " ";
                        })}
                      </td>
                      <td>
                        <div className="button">
                          <button
                            className="courseBtn"
                            onClick={() => this.onDelete(item._id)}
                          >
                            Remove Course
                          </button>
                        </div>
                      </td>
                    </tr>
                  );
                })}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}
