import React, { Component } from "react";
import "../css/style.css";
import axios from "axios";
import Cookie from "js-cookie";
import Magnifier from "../img/magnifier.png";

export default class Courses extends Component {
  constructor(props) {
    super(props);

    this.updateTable = this.updateTable.bind(this);
    //this.onChangeCourses = this.onChangeCourses.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    //this.onAddItem = this.onAddItem.bind(this);
    //this.changeLooks = this.changeLooks.bind(this);
    this.addCourse = this.addCourse.bind(this);
    this.onSearch = this.onSearch.bind(this);
    this.setupForAdding = this.setupForAdding.bind(this);

    this.state = {
      data: [],
      page: 1,
      courses: [],
      //course: "",
      userId: "",
      UserCourses: [],
      options: [],
      selected: undefined,
      type: "Regular",
      order: "Primary",
      ref: React.createRef(),
    };
  }

  //Right before anything load the page this is called
  async componentDidMount() {
    await this.updateTable();
  }

  async updateTable() {
    const response = await fetch(
      "http://localhost:5000/courses?page=" +
        this.state.page +
        "&token=" +
        Cookie.get("token") +
        "&userId=" +
        Cookie.get("userId")
    );
    const res = await response.json();

    this.setState({
      data: res.array,
    });
  }

  /*  onChangeCourse = (event) => {
    this.setState({ course: event.target.value });
  }; */

  /* onAddItem(course) {
    this.setState((state) => {
      const courses = [...state.courses, course];
      return {
        courses,
      };
    });
  } */
  setupForAdding(e) {
    this.setState({
      selected: e,
    });
    this.addCourse();
  }

  addCourse() {
    if (this.state.selected === undefined) return;

    this.state.UserCourses.push({
      order: this.state.order,
      type: this.state.type,
      course_id: this.state.selected[0]._id,
      code: this.state.selected[0].label.split(" ")[0],
    });
    this.setState({
      UserCourses: this.state.UserCourses,
      selected: undefined,
      options: [],
    });

    this.state.ref.current.clear();
    document.getElementById("coursesSelection");
  }

  /*  onChangeCourses(e) {
    this.state.courses.push(e);
  } */

  onSubmit(e) {
    e.preventDefault();
    console.log("Submitting");
    console.log(this.state.UserCourses);

    const userCourses = {
      userId: Cookie.get("userId"),
      courses: this.state.UserCourses,
    };

    console.log(userCourses);

    axios
      .post(
        "http://localhost:5000/users/setUserCourses?token=" +
          Cookie.get("token") +
          "&userId=" +
          Cookie.get("userId"),
        userCourses
      )
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        alert("Error: " + err);
      });

    /* e.preventDefault();
    const added = {
      userId: Cookie.get("userId"),
      courses: this.state.courses,
    };
    axios
      .post(
        "http://localhost:5000/users/setUserCourses?token=" +
          Cookie.get("token") +
          "&userId=" +
          Cookie.get("userId"),
        added
      )
      .then((res) => {
        console.log("Success");
      })
      .catch((err) => alert("Error: " + err)); */
  }
  // Search impelmentation
  onSearch(e) {
    e.preventDefault();
    const added = {
      coursID: this.state.courseID,
    };
    axios
      .post(
        "http://localhost:5000/courses/search?token=" +
          Cookie.get("token") +
          "&userId=" +
          Cookie.get("userId"),
        added
      )
      .then((res) => {
        console.log("Success");
      })
      .catch((err) => alert("Error: " + err));
  }

  /*  changeLooks(e) {
    var btn = document.querySelector(".downBtn");
    btn.style.background = "#292626";
  } */

  render() {
    return (
      <div className="addCourses">
        <div className="menu">
          <div className="line1"></div>
          <div className="line2"></div>
          <div className="line3"></div>
        </div>
        <div className="upperNav">
          <div className="search">
            <form className="searchForm">
              <input type="search" name="search" placeholder="Search" />
            </form>
            <button className="magnifier">
              <img src={Magnifier} className="manifierImg" />
            </button>
          </div>
          <div className="prevNext">
            <button> P </button>
            <button> N </button>
          </div>
        </div>
        <div className="addCoursesDownNav">
          <div className="typeOfCourses">
            <a className="blueBorder" href="addCourses.html">
              All courses
            </a>
            <a href="mandatoryCourses.html">Mandatory courses</a>
            <a href="electiveCourses.html">Elective courses</a>
          </div>
        </div>
        <div className="allCourses" id="allCourses">
          <form onSubmit={this.onSubmit} className="allCoursesForm">
            <div className="buttonAbove">
              <button id="coursesBtn">Confirm Selection</button>
            </div>
            <table className="courses">
              <tbody>
                <tr className="info">
                  <td>Course Code</td>
                  <td>Course Name</td>
                  <td>Professor</td>
                  <td>Faculty</td>
                  <td>Prerequisites</td>
                  <td>Add course</td>
                </tr>
                {this.state.data.map((item, i) => {
                  return (
                    <tr key={i}>
                      <td className="title">{item.course_id}</td>
                      <td>
                        <a href={"https://ecampus.ius.edu.ba/" + item.Url}>
                          {item.course_name}
                        </a>
                      </td>
                      <td>{item.Lecturer}</td>
                      <td>{item.AcademicUnit}</td>
                      <td>
                        {item.prerequisite.map((item) => {
                          return item + " ";
                        })}
                      </td>
                      <td>
                        <div className="button">
                          <button
                            className="downBtn"
                            onClick={
                              () => this.setupForAdding(item)
                              //this.changeLooks()
                            }
                          >
                            Add Course
                          </button>
                        </div>
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </form>
        </div>
      </div>
    );
  }
}
