import React, { Component } from "react";
//import { Link } from 'react-router-dom';
import { Navbar, Nav, NavDropdown, Button } from "react-bootstrap";
import "C:/Users/USER/Desktop/online_enrollment-master/src/css/main.css";
import "C:/Users/USER/Desktop/online_enrollment-master/src/css/Montserrat-Regular.ttf";
import Cookie from "js-cookie";
import axios from "axios";

const btnStyle = {
  margin: "5px",
};

export default class Navigation extends Component {
  constructor(props) {
    super(props);

    this.logout = this.logout.bind(this);

    this.state = {
      isLogin: false,
      buttons: "",
    };
  }

  async componentDidMount() {
    /*Check if the user is properly authenticated*/
    if (!Cookie.get("token")) {
      this.setState({
        buttons: (
          <div>
            <Button variant="success" style={btnStyle} href="/login">
              Login
            </Button>
            <Button variant="outline-success" style={btnStyle} href="/signup">
              Sign up
            </Button>
          </div>
        ),
      });
      return;
    }

    const response = await fetch(
      "http://localhost:5000/users/auth?token=" +
        Cookie.get("token") +
        "&userId=" +
        Cookie.get("userId")
    );

    if (response.status === 200)
      this.setState({
        buttons: (
          <Button variant="success" style={btnStyle} onClick={this.logout}>
            logout
          </Button>
        ),
      });
    else {
      this.setState({
        buttons: (
          <div>
            <Button variant="success" style={btnStyle} href="/login">
              Login
            </Button>
            <Button variant="outline-success" style={btnStyle} href="/signup">
              Sign up
            </Button>
          </div>
        ),
      });
    }
  }

  logout() {
    axios
      .get(
        "http://localhost:5000/users/logout?token=" +
          Cookie.get("token") +
          "&userId=" +
          Cookie.get("userId")
      )
      .then((res) => {
        Cookie.remove("token");
        Cookie.remove("userId");
        window.location = "/";
      })
      .catch((err) => {
        /*alert("Error: " + err);*/ //Very strange beacause we considere it has an error but it is not...
        Cookie.remove("token");
        Cookie.remove("userId");
        window.location = "/";
      });
  }

  render() {
    return (
      <nav class="navigation">
        <div class="logo">
          <img class="ius" src="img/IUSlogo2.png" />
          <span class="user">Adem Dugalić</span>
        </div>
        <div class="upButtons">
          <div class="links">
            <a id="home" href="index.html">
              <span data-hover="Home">Home</span>
            </a>
            <a id="MyCourses" href="myCourses.html">
              <span data-hover="MyCourses">My courses</span>
            </a>
            <a id="AddCourses" href="addCourses.html">
              <span data-hover="AddCourses">Add courses</span>
            </a>
            <a id="Schedule" href="schedule.html">
              <span data-hover="Schedule">Schedule</span>
            </a>
          </div>
        </div>
        <div class="downButtons">
          <div class="links">
            <a id="LogOut" href="#">
              <span data-hover="LogOut">Log Out</span>
            </a>
          </div>
        </div>
      </nav>
    );
  }
}
