import React from "react";
import ReactDOM from "react-dom";
import App from "../App";
import "src/vendor/jquery/jquery-3.2.1.min.js";

ReactDOM.render(<App />, document.getElementById("root"));
var nav = document.querySelector(".navigation");
var logo = document.querySelector(".logoTrans");
var upButtons = document.querySelector(".upButtons");
var downButtons = document.querySelector(".downButtons");
var logOut = document.querySelector("#LogOut");
var user = document.querySelector(".user");
var switch1 = document.querySelector(".switch");
var iusTrans = document.querySelector(".iusTrans");
var menu = document.querySelector(".menu");
var navLinks = document.querySelector(".links");
var flag = true;
var cards = document.getElementsByClassName("card");

if (window.innerWidth <= 768) {
  nav.style.display = "none";
  for (var i = 0; i < cards.length; i++) {
    if (cards.item(i).innerHTML == "") {
      cards.item(i).remove();
    }
  }
}

menu.addEventListener("click", function () {
  if (flag) {
    nav.style.display = "";
    flag = false;
  } else {
    nav.style.display = "none";
    flag = true;
  }
});

(function ($) {
  "use strict";

  /*==================================================================
  [ Focus input ]*/
  $(".input100").each(function () {
    $(this).on("blur", function () {
      if ($(this).val().trim() != "") {
        $(this).addClass("has-val");
      } else {
        $(this).removeClass("has-val");
      }
    });
  });

  /*==================================================================
  [ Validate ]*/
  var input = $(".validate-input .input100");

  $(".validate-form").on("submit", function () {
    var check = true;

    for (var i = 0; i < input.length; i++) {
      if (validate(input[i]) == false) {
        showValidate(input[i]);
        check = false;
      }
    }

    return check;
  });

  $(".validate-form .input100").each(function () {
    $(this).focus(function () {
      hideValidate(this);
    });
  });

  function validate(input) {
    if ($(input).attr("type") == "email" || $(input).attr("name") == "email") {
      if (
        $(input)
          .val()
          .trim()
          .match(
            /^([a-zA-Z0-9_\-\.]+)@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.)|(([a-zA-Z0-9\-]+\.)+))([a-zA-Z]{1,5}|[0-9]{1,3})(\]?)$/
          ) == null
      ) {
        return false;
      }
    } else {
      if ($(input).val().trim() == "") {
        return false;
      }
    }
  }

  function showValidate(input) {
    var thisAlert = $(input).parent();

    $(thisAlert).addClass("alert-validate");
  }

  function hideValidate(input) {
    var thisAlert = $(input).parent();

    $(thisAlert).removeClass("alert-validate");
  }

  /*==================================================================
  [ Show pass ]*/
  var showPass = 0;
  $(".btn-show-pass").on("click", function () {
    if (showPass == 0) {
      $(this).next("input").attr("type", "text");
      $(this).addClass("active");
      showPass = 1;
    } else {
      $(this).next("input").attr("type", "password");
      $(this).removeClass("active");
      showPass = 0;
    }
  });
})(jQuery);
