import React, { Component } from "react";
import axios from "axios";
import Cookie from "js-cookie";
import { Link } from "react-router-dom";
import Magnifier from "../img/magnifier.png";

export default class UserCoursesCurrect extends Component {
  constructor(props) {
    super(props);

    this.onInputChange = this.onInputChange.bind(this);
    this.addCourse = this.addCourse.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.removeCourse = this.removeCourse.bind(this);

    this.state = {
      UserCourses: [],
      options: [],
      selected: undefined,
      type: "Regular",
      order: "Primary",
      ref: React.createRef(),
    };
  }

  //Right before anything load the page this is called
  componentDidMount() {
    axios
      .get(
        "http://localhost:5000/users/getUserCourse?token=" +
          Cookie.get("token") +
          "&userId=" +
          Cookie.get("userId")
      )
      .then((res) => {
        this.setState({ UserCourses: res.data.courses });
      })
      .catch((err) => {
        alert("Error: " + err);
      });
  }

  //InputChange for the course box
  onInputChange(e) {
    if (e.length < 3) return;

    axios
      .get(
        "http://localhost:5000/courses/search?value=" +
          e +
          "&token=" +
          Cookie.get("token") +
          "&userId=" +
          Cookie.get("userId")
      )
      .then((res) => {
        var options = [];

        res.data.forEach((item, index) => {
          options.push({
            id: item._id,
            label: item.course_id + " " + item.course_name,
          });
        });
        this.setState({ options: options });
      })
      .catch((err) => {
        alert("Error: " + err);
      });
  }

  removeCourse(index) {
    console.log(index);
    this.state.UserCourses.splice(index, 1);
    this.setState({ UserCourses: this.state.UserCourses });
  }

  render() {
    return (
      <div className="myCourses">
        <div className="menu">
          <div className="line1"></div>
          <div className="line2"></div>
          <div className="line3"></div>
        </div>
        <div className="upperNav">
          <div className="search">
            <form className="searchForm">
              <input type="search" name="search" placeholder="Search" />
            </form>
            <button className="magnifier">
              <img src={Magnifier} className="manifierImg" />
            </button>
          </div>
          <div className="prevNext">
            <button>P</button>
            <button>N</button>
          </div>
        </div>
        <div className="downNav">
          <div className="passedCurrent">
            <Link id="passedC" to="/UserCourses">
              Passed courses
            </Link>
            <a id="currentC">Current courses</a>
          </div>
        </div>
        <div className="passedCourses">
          <table className="semester">
            <thead>
              <tr>
                <th className="semesterNum" colSpan="5">
                  Taken Courses
                </th>
              </tr>
            </thead>
            <tbody>
              <tr className="info">
                <td>Course Code</td>
                <td>Course Name</td>
                <td>Pofessor</td>
                <td>Academic Unit</td>
                <td>Pre req</td>
              </tr>
              {this.state.UserCourses.map((item, i) => {
                return (
                  <tr key={i}>
                    <td className="title">{item.course_id}</td>
                    <td>
                      <a href={"https://ecampus.ius.edu.ba/" + item.Url}>
                        {item.course_name}
                      </a>
                    </td>
                    <td>{item.Lecturer}</td>
                    <td>{item.AcademicUnit}</td>
                    <td>
                      {item.prerequisite.map((item) => {
                        return item + " ";
                      })}
                    </td>
                    <td>
                      <div className="button">
                        <button onClick={() => this.onDelete(item.course_id)}>
                          Remove Course
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}
